import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import * as BooksAPI from "./utils/BooksAPI";
import escapeRegExp from 'escape-string-regexp'
import BookShelfChanger from "./BookShelfChanger";


class SearchBooks extends Component {

  constructor(props) {
    super(props);
    this.state = {
      query: "",
      library: []
    }
  }

  // Function to be used in onChange in HTML input field.
  updateQuery = (query) => {
    let editedQuery = query.trim();
    this.setState({
      query: editedQuery,
      // library: this.searchApi(editedQuery)
    });
    this.searchApi(this.state.query);
  };

  searchApi = (query) => {
    let library;
    library = BooksAPI.search(query).then(
      library => {
        // console.log(library);
        this.setState({
          library: library
        })
        // this.setState({library: library})
      }
    );


  };

  render(){
    // const { query } = this.state;
    const { query, library } = this.state;

    // Use the search method to
    // let library;
    // library = this.searchApi(query);

    // Filter query section
    // let showingBooks;
    // if (this.state.query){
    //   const match = new RegExp(escapeRegExp(this.state.query), 'i');
    //   showingBooks = this.state.library.filter( book => match.test(book.name));
    // }
    console.log(library);

    return <div className="search-books">
      <div className="search-books-bar">
        {/*<button className="close-search" onClick={() => this.setState({ showSearchPage: false })}>Close</button>*/}
        <Link to="/" className="close-search"/>
        <div className="search-books-input-wrapper">
          {/*
                  NOTES: The search from BooksAPI is limited to a particular set of search terms.
                  You can find these search terms here:
                  https://github.com/udacity/reactnd-project-myreads-starter/blob/master/SEARCH_TERMS.md

                  However, remember that the BooksAPI.search method DOES search by title or author. So, don't worry if
                  you don't find a specific author or title. Every search is limited by search terms.
                */}
          <input
            type="text"
            value={query}
            onChange={(event) => this.updateQuery(event.target.value)}
            // onChange={(event) => this.searchApi(event.target.value)}
            placeholder="Search by title or author"/>

        </div>
      </div>
      <div className="search-books-results">
        <ol className="books-grid">
          { // Building a list:
            (library && library.length > 0 && (library.map(book => (
              <li key={book.id}>
                <div className="book">
                  <div className="book-top">
                    <div className="book-cover"
                         style={{ width: 128, height: 193, backgroundImage: `url(${book.imageLinks.smallThumbnail})`}}>
                      {/*<BookShelfChanger*/}
                      {/*currentShelf={book.shelf}*/}
                      {/*onUpdateBook={(shelf) => { this.updateBook(book, shelf); }}*/}
                      {/*/>*/}
                    </div>
                  </div>
                  <div className="book-title">{book.title}</div>
                  {/*<div className="book-authors">{book.authors.join(", ")}</div>*/}
                </div>
              </li>
            ))))
          }
        </ol>
      </div>
    </div>
  }
}

export default SearchBooks;
