import React, { Component } from 'react';
import { Route } from 'react-router-dom'
import logo from './logo.svg';
import './App.css';
import SearchBooks from './SearchBooks'
import ListBooks from './ListBooks'
import * as BooksAPI from './utils/BooksAPI'

class BooksApp extends Component {
  state = {
    books: []
  };

  // Lifecycle event, fetches contacts from the server
  componentDidMount(){
    BooksAPI.getAll().then((books)=>{
      this.setState({books: books})
      // console.log(this.state);
    })
  }

  updateBook = (book, shelf) => {
    BooksAPI.update(book, shelf);
    this.setState( state => {
      console.log("Just ran updateBook");
      console.log(this.state.books);
      //  Find the same book in state that is being passed and change its shelf value.
      books : state.books.map( (b) => {
          if (b.id === book.id){
            b.shelf = shelf;
          }
          return b;
        }
      )
    });


  };


  render() {
    //
    // let currentlyReading;
    // let wantToRead;
    // let read;

    // currentlyReading = this.state.books.filter(book => {
    //   return book.shelf === "currentlyReading";
    // });
    //
    // wantToRead = this.state.books.filter(book => {
    //   return book.shelf === "wantToRead"
    // });
    //
    // read = this.state.books.filter(book => {
    //   return book.shelf === "read"
    // });

    // console.log(currentlyReading, wantToRead, read);

    return (
      <div className="app">
        <Route path='/search' render={ () => (
          <SearchBooks/>
        )}/>
        <Route exact path='/' render={ () => (
          <ListBooks
            books={this.state.books}
            // currentlyReading={currentlyReading}
            // wantToRead={wantToRead}
            // read={read}
            onUpdateBook={this.updateBook}/>
        )}/>
        {/* Old: {this.state.showSearchPage ? ( <SearchBooks/>) : (<ListBooks/>)}*/}
      </div>
    )
  }
}

export default BooksApp;
