import React, { Component } from 'react'
import PropTypes from 'prop-types'

class BookShelfChanger extends Component {
  static propTypes = {
    book: PropTypes.object.isRequired
  };

  //TODO: make it into a functional component instead of a class
  render(){

    const { onUpdateBook } = this.props;

    return <div className="book-shelf-changer">
      <select>
        <option value="move" disabled>Move to...</option>
        <option value="currentlyReading"
                onSelect={() => onUpdateBook(this.props.book, "currentlyReading")}>Currently Reading</option>
        <option value="wantToRead"
                onSelect={() => onUpdateBook(this.props.book, "wantToRead")}>Want to Read</option>
        <option value="read"
                onSelect={() => onUpdateBook(this.props.book, "read")}>Read</option>
        <option value="none">None</option>
      </select>
    </div>
  }
}

export default BookShelfChanger;
