import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import BookShelfChanger from './BookShelfChanger'
import PropTypes from 'prop-types'
import * as BooksAPI from "./utils/BooksAPI";


class ListBooks extends Component {

  static propTypes = {
    books: PropTypes.array.isRequired,
    // currentlyReading: PropTypes.array.isRequired,
    // wantToRead: PropTypes.array.isRequired,
    // read: PropTypes.array.isRequired,
    // onUpdateBook: PropTypes.func.isRequired
  };

  constructor(props) {
    super(props);
    this.state = {
      currentlyReading: this.props.books.filter(book => {
        return book.shelf === "currentlyReading";
      }),
      wantToRead: this.props.books.filter(book =>
        book.shelf === "wantToRead"
      ),
      read: this.props.books.filter(book => {
        return book.shelf === "read"
      })
    }
  }

  updateBook = (book, shelf) => {
    BooksAPI.update(book, shelf);


    // this.setState( state => {
    //   //  Find the same book in state that is being passed and change its shelf value.
    //   books : state.books.map( (b) => {
    //       if (b.id === book.id){
    //         b.shelf = shelf;
    //       }
    //       return b;
    //     }
    //   )
    // });
    // console.log("Just ran updateBook");
    // console.log(this.state.books);
  };

  render() {
    // const { onUpdateBook } = this.props;
    const { currentlyReading, wantToRead, read } = this.state;

    // let currentlyReading;
    // let wantToRead;
    // let read;
    //
    // currentlyReading = this.props.books.filter(book => {
    //   return book.shelf === "currentlyReading";
    // });
    //
    // wantToRead = this.props.books.filter(book => {
    //   return book.shelf === "wantToRead"
    // });
    //
    // read = this.props.books.filter(book => {
    //   return book.shelf === "read"
    // });

    // TODO: Need some kind of event listener for BookShelfChanger

    return <div className="list-books">
      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>
      <div className="list-books-content">
        <div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Currently Reading</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                { // Building a list:
                  currentlyReading.map(book => (
                    <li key={book.id}>
                      <div className="book">
                        <div className="book-top">
                          <div className="book-cover"
                               style={{ width: 128, height: 193, backgroundImage: `url(${book.imageLinks.smallThumbnail})`}}>
                            <BookShelfChanger
                              currentShelf={book.shelf}
                              // onShelfChange={(shelf) => { onUpdateBook(book, shelf); }}
                              onUpdateBook={(shelf) => { this.updateBook(book, shelf); }}

                            />
                          </div>
                        </div>
                        <div className="book-title">{book.title}</div>
                        <div className="book-authors">{book.authors.join(", ")}</div>
                      </div>
                    </li>
                  ))
                }
              </ol>
            </div>
          </div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Want to Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                { // Building a list:
                  wantToRead.map(book => (
                    <li key={book.id}>
                      <div className="book">
                        <div className="book-top">
                          <div className="book-cover"
                               style={{ width: 128, height: 193, backgroundImage: `url(${book.imageLinks.smallThumbnail})`}}>
                            <BookShelfChanger currentShelf={book.shelf}
                                              onUpdateBook={(shelf) => { this.updateBook(book, shelf); }}
                                              // onShelfChange={(shelf) => onUpdateBook(book, shelf)}
                            />
                          </div>
                        </div>
                        <div className="book-title">{book.title}</div>
                        <div className="book-authors">{book.authors.join(", ")}</div>
                      </div>
                    </li>
                  ))
                }
              </ol>
            </div>
          </div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                { // Building a list:
                  read.map(book => (
                    <li key={book.id}>
                      <div className="book">
                        <div className="book-top">
                          <div className="book-cover"
                               style={{ width: 128, height: 193, backgroundImage: `url(${book.imageLinks.smallThumbnail})`}}>
                            <BookShelfChanger
                              currentShelf={book.shelf}
                              onUpdateBook={(shelf) => { this.updateBook(book, shelf); }}
                              // onShelfChange={(shelf) => onUpdateBook(book, shelf)}
                            />
                          </div>
                        </div>
                        <div className="book-title">{book.title}</div>
                        <div className="book-authors">{book.authors.join(", ")}</div>
                      </div>
                    </li>
                  ))
                }
              </ol>
            </div>
          </div>
        </div>
      </div>
      <div className="open-search">
        {/*<button onClick={() => this.setState({ showSearchPage: true })}>Add a book</button>*/}
        <Link to="/search" ><button>Add a book</button></Link>
      </div>
    </div>
  }
}

export default ListBooks;


