import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import PropTypes from 'prop-types'
import { throttle } from "throttle-debounce";
import FilterChanger from './FilterChanger'
import * as FoursquareAPI from './utils/FoursquareAPI'


class SearchField extends Component {

  static propTypes = {
    tapas: PropTypes.array,
    location: PropTypes.object.isRequired,
    update: PropTypes.func.isRequired
  };

  constructor(props) {
    super(props);
    this.state = {
      query: ""
    };
    this.autocompleteSearchThrottled = throttle(500, this.autocompleteSearch);
  }

  updateQuery = (e) =>{
    if (e.key === 'Enter') {
      this.setState({
          query: e.target.value},
        () => { this.autocompleteSearchThrottled(this.state.query) }
      );
    }

  };

  autocompleteSearch = q => {
    this.searchApi()
  };

  // Search API with a specific query/filter
  // update() is called in App.js, to update TapasList component
  searchApi = () => {
    FoursquareAPI.getQuery(this.state.query, this.props.location)
      .then(jsonRes => {
        console.log("getQuery response ", jsonRes.response);
        this.props.update(jsonRes.response.venues);
        }
      )
  };

  render() {
    return (<div className="search-menu">
      <div className="search-book-bar">
        <div className="form-group">
          <input className="form-control"
                 type="text"
                 value={this.state.query}
                 onKeyPress={this.updateQuery}
                 // onChange={(event) => this.updateQuery(event)}
                 placeholder="Search by cuisine type"/>
        </div>
      </div>
      <FilterChanger/>


    </div> )
  }
}

export default SearchField;

// Notes:
// Resources to develop the throttle functionality:
// https://www.peterbe.com/plog/how-to-throttle-and-debounce-an-autocomplete-input-in-react
