/* global google */
import React, { Component } from 'react';
import PropTypes from "prop-types";

// Sources:
// https://stackoverflow.com/questions/48493960/using-google-map-in-react-component  (best)
// https://stackoverflow.com/questions/45429484/how-to-implement-google-maps-js-api-in-react-without-an-external-library
// https://jsbin.com/tejutihoka/edit?js,output

class MapPromise extends Component {

  static propTypes = {
    tapas: PropTypes.array,
    checkedId: PropTypes.string
  };

  constructor(props){
    super(props);
    this.state = {
      mapIsReady: false,
      map: "",
      infoWindow: ""
    };
  }

  getGoogleMaps() {
    // If we haven't already defined the promise, define it
    if (!this.googleMapsPromise) {
      this.googleMapsPromise = new Promise((resolve) => {
        // TODO: Write a reject method!
        // Add a global handler for when the API finishes loading
        window.resolveGoogleMapsPromise = () => {
          // Resolve the promise
          resolve(google);

          // Tidy up
          delete window.resolveGoogleMapsPromise;
        };

        // Load the Google Maps API
        const script = document.createElement("script");
        const API = 'AIzaSyB8uwCpGazZQ4JvdwNQP9Jt0GH0FukWd-M';
        script.src = `https://maps.googleapis.com/maps/api/js?key=${API}&callback=resolveGoogleMapsPromise`;
        script.async = true;
        document.body.appendChild(script);
      });
    }

    // Return a promise for the Google Maps API
    return this.googleMapsPromise;
  }

  // Method used on each marker upon creation
  populateInfoWindow = (marker, infoWindow, map) => {

    if (infoWindow.marker != marker){
      console.log("Enter populate");

      infoWindow.marker = marker;
      infoWindow.setContent(`<div>${marker.title}</div>`);
      infoWindow.open(map, marker);

      infoWindow.addListener('closeclick', ()=>{
        infoWindow.marker = null;
      })
    }
  };

  componentWillMount() {
    // Start Google Maps API loading since we know we'll soon need it
    this.getGoogleMaps();
    // console.log("tapas 1", this.props.tapas);
  }

  componentDidMount() {
    // Once the Google Maps API has finished loading, initialize the map
    // NOTE: Moved content to componentDidUpdate() because props was still empty.
    // console.log("tapas 2", this.props.tapas);
    console.log("Start ComponentDidMount");

    this.getGoogleMaps().then((google) => {
      const center = {lat: 50.121870, lng: 8.689550};
      const map = new google.maps.Map(document.getElementById('map'), {
        zoom: 13,
        center: center,
        mapTypeControl: false
      });

      let largeInfoWindow = new google.maps.InfoWindow();

      console.log("infowindow:" , largeInfoWindow)

      return {map:map, window: largeInfoWindow};

    }).then(obj => {
      this.setState({
        map: obj.map,
        infoWindow: obj.window
      })
    })

  }

  componentDidUpdate(){
    console.log("Start ComponentDidUpdate");

    const {tapas, checkedId} = this.props;
    const {map, infoWindow} = this.state;
    let markers;

    let bounds = new google.maps.LatLngBounds();

    markers = tapas.map(tapa => {
      let marker = new google.maps.Marker({
        map: map,
        position: {lat: tapa.venue.location.lat, lng: tapa.venue.location.lng},
        title: tapa.venue.name,
        // animation: google.maps.Animation.DROP,
        id: tapa.venue.id
      });
      marker.addListener('click', () => {
        this.populateInfoWindow(marker, infoWindow, map);
      });

      bounds.extend(marker.position);

      // if (marker.id === checkedId){
      //   this.populateInfoWindow(marker, infoWindow, map);
      // } else {
      //   infoWindow.close();
      // }

      return marker;
    });



    // Once the Google Maps API has finished loading,
    // AND props loaded -> initialize the map
    // this.getGoogleMaps().then((google) => {
    //
    //   const center = {lat: 50.121870, lng: 8.689550};
    //   const map = new google.maps.Map(document.getElementById('map'), {
    //     zoom: 13,
    //     center: center,
    //     mapTypeControl: false
    //   });
    //
    //   let largeInfoWindow = new google.maps.InfoWindow();
    //   let bounds = new google.maps.LatLngBounds();
    //
    //   markers = tapas.map(tapa => {
    //     let marker = new google.maps.Marker({
    //       map: map,
    //       position: {lat: tapa.venue.location.lat, lng: tapa.venue.location.lng},
    //       title: tapa.venue.name,
    //       // animation: google.maps.Animation.DROP,
    //       id: tapa.venue.id
    //     });
    //     marker.addListener('click', () => {
    //       this.populateInfoWindow(marker, largeInfoWindow, map);
    //     });
    //     bounds.extend(marker.position);
    //
    //     if (marker.id === this.props.checkedId){
    //       console.log("this", this);
    //       this.populateInfoWindow(marker, largeInfoWindow, map);
    //     }
    //
    //     return marker;
    //   });
    //
    //   // map.fitBounds(bounds)
    //   console.log("Markers: ", markers);
    //
    // });
  }

  render() {
    return (
      <div>
        <h1>Filtered Locations</h1>
        <div id="map" style={{width: 400, height: 300}}></div>
      </div>
    )
  }
}

export default MapPromise;
