import React, {Component} from 'react'
import PropTypes from 'prop-types'



class TapasList extends Component {

  static propTypes = {
    tapas: PropTypes.array.isRequired,
    onCheckedId: PropTypes.func
    // photos_ids: PropTypes.array.isRequired,
    // photos: PropTypes.array
  };

  constructor(props){
    super(props)
  }

  getVenuePhoto = (tapa) => {

    if (tapa.photos && tapa.photos.photo_res.length > 0) {
      let prefix = tapa.photos.photo_res[0].prefix;
      let suffix  = tapa.photos.photo_res[0].suffix;
      return `${prefix}300x300${suffix}`;
    } else {
      return `https://via.placeholder.com/300`;
    }
  };

  handleSelection(e){
    this.props.onCheckedId(e.currentTarget.dataset.id);
    document.getElementById(e.currentTarget.dataset.id).classList.add("highlight");
  // TODO:  Change card color to #f8f8ff on selection
  }

  render() {

    const {tapas} = this.props;

    return (
      <div className="">
        <div className="list-container">
        <ul className="list-group">
          { // Builing a list
            (tapas && tapas.length > 0 && (tapas.map( tapa => (
              <li key={tapa.referralId} className='card' onClick={this.handleSelection.bind(this)} data-id={tapa.venue.id}>
                <div className='card-body' id={tapa.venue.id}>
                  <img className="photo rounded float-left img-fluid"
                       src={this.getVenuePhoto(tapa)} alt={`Photo of ${tapa.venue.name}`} width="300" height="300"/>
                  <div className="card-body">
                    <h5 className="card-title mb-1">{tapa.venue.name}</h5>
                    <p className="font-weight-normal mb-0">Address: {tapa.venue.location.address}</p>
                    <p className="font-weight-light mb-0">Category: {tapa.venue.categories[0].name}</p>
                    <p className="font-weight-light font-italic mb-0"> {tapa.reasons.items[0].summary}</p>
                  </div>
                </div>
              </li>
            ))))
        }
        </ul>
        </div>
      </div>
    )
  }
}
// `https://via.placeholder.com/300`
// 0: {id: "56a91ce238fae81cf0c4b47e", createdAt: 1453923554, source: {…}, prefix: "https://fastly.4sqi.net/img/general/", suffix: "/67930521_I6ZtnHrfX_kWjvUDTd1Zh67o1fBBjZvk0Whvh_pZA4E.jpg", …}
export default TapasList;
