import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import SearchField from './SearchField'
import MapField from './MapField'
import * as FourAPI from './utils/FoursquareAPI'

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      query: "",
      restaurants: [],
      location: {
        lat: 50.121870,
        lng: 8.689550
      }
    }
  }

  // Lifecycle event, fetches restaurants from FourSquare API
  componentDidMount(){
    FourAPI.getAll().then( (restaurants) => {
      this.setState({restaurants: restaurants})
    })
  }

  render() {
    return (
      <div>
        <div className="row">
          <div className="col-12 col-md-6">
            <SearchField/>
            {/*<ListRestaurants/>*/}
          </div>
          <div className="col-12 col-md-6 map-container">
            <MapField/>
          </div>
        </div>
      </div>
    );
  }
}

export default App;


