import React, { Component } from 'react'
import PropTypes from 'prop-types'
import FilterButton from './FilterButton'

class FilterNumber extends Component {
  // Button's source:
  //  https://blog.logrocket.com/how-to-use-bootstrap-with-react-a354715d1121

  // Foursquare
  // API: https://developer.foursquare.com/docs/api
  // https://de.foursquare.com/explore?mode=url&near=Nordend-West%2C%20Frankfurt%20am%20Main&nearGeoId=72090&novelty=new&q=Essen

  static propTypes = {
    tapasShown: PropTypes.array,
    onNumberUpdate: PropTypes.func
  };

  constructor(props) {
    super(props);
    // this.state = {};
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    // this.props.onUpdateBook(event.target.value);
    // this.setState({value: event.target.value});
  }

  chooseNumber = (nr, evt) => {
    evt.preventDefault();
    console.log(evt);
    this.props.onNumberUpdate(nr);
  };

  loadButtons = () => {
    let expr = this.props.tapasShown.length;
    switch (expr) {
      case expr < 11:
        return (
          <div className="dropdown-menu">
            <FilterButton items={this.props.tapasShown.length}/>
          </div>);
      case expr < 21:
        return (
          <div className="dropdown-menu">
            <FilterButton items={10}/>
            <FilterButton items={this.props.tapasShown.length}/>
          </div>)
      case expr < 31:
        return (
        <div className="dropdown-menu">
          <FilterButton items={10}/>
          <FilterButton items={20}/>
          <FilterButton items={this.props.tapasShown.length}/>
        </div>);
      case expr < 41:
        return (
          <div className="dropdown-menu">
            <FilterButton items={10}/>
            <FilterButton items={20}/>
            <FilterButton items={30}/>
            <FilterButton items={this.props.tapasShown.length}/>
          </div>);
      case expr < 51:
        return (
          <div className="dropdown-menu">
            <FilterButton items={10}/>
            <FilterButton items={20}/>
            <FilterButton items={30}/>
            <FilterButton items={40}/>
            <FilterButton items={this.props.tapasShown.length}/>
          </div>);
    }
  };

  render(){

    const { tapasShown } = this.props;
    // const themeClass = theme ? theme.toLowerCase() : 'secondary';


    return <div className="search-menu">
        <div className="btn-group">
          <button type="button" className="btn btn-secondary">Showing { tapasShown.length || '0' } results</button>
          <button type="button" className="btn btn-secondary dropdown-toggle dropdown-toggle-split" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            <span className="sr-only">Toggle Results Dropdown</span>
          </button>
          {this.loadButtons()}
        </div>
      </div>


  }
}

export default FilterNumber;

