  //   .then( res => res.json() )
  //   .catch( err => {
  //     console.log("Photo error", err)
  //   })
};

export const getQuery = (quer) => {

  const api = "https://api.foursquare.com/v2/venues/explore?";

  let myHeaders = new Headers();

  const myInit = {
    method: 'GET',
    headers: myHeaders,
    mode: 'cors',
    cache: 'default' };

  myHeaders.append("Accept-Language", "en");

  fetch(api, myInit)
    .then((res) => res.json())
    .catch(function() {
      // Code for handling errors
    });
};
