import React, { Component } from 'react';
import { Route } from 'react-router-dom'
import logo from './logo.svg';
import './App.css';
import SearchField from './SearchField'
import MapField from './MapField'
import TapasList from './TapasList'
import Favourites from './Favourites'
import * as FoursquareAPI from './utils/FoursquareAPI'
// import {hardTapas} from "./utils/FoursquareAPI";


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      query: "",
      tapas: [], //hardTapas,
      location: {
        lat: 50.121870,
        lng: 8.689550
      },
      photos_ids: [],
      photos: []
    }
  }

  // Lifecycle event, fetches restaurants from FourSquare API
  componentDidMount(){
    FoursquareAPI.getTapas(this.state.location)
      .then( myJson => {
        // console.log("Response:", myJson.response );
        this.setState({tapas: myJson.response.groups[0].items},
          () => {this.addPhotos()});
    }).catch( err => { console.log(err)} )
  }

  addPhotos = () => {
    let idArray;
    let temp_photos = [];
    // Clone tapas (deep copy)
    let temp_tapas = JSON.parse(JSON.stringify(this.state.tapas));

    // If explored responses loaded from API,
    // search the API again for the venue photos
    // Not most efficient, but the explore-API returns no images in json obj.
    if (this.state.tapas){
      idArray  = this.state.tapas.map( tapa => tapa.venue.id );

      // this.setState({photos_ids: idArray});

      FoursquareAPI.getPhotos(idArray)
        .then( jsonResolvedArr => {
          // Create array of resolved promises' responses array
          jsonResolvedArr.map( (promise, indx) => (
            promise.then( res => {
              // console.log(idArray[indx]);
              // temp_photos.push({photos: {id: idArray[indx], photo_res: res.response.photos.items}} )
              temp_tapas[indx].photos = {id: idArray[indx], photo_res: res.response.photos.items}
            })));
        }) // Refresh state only after all photos addresses have been loaded
        .then( () => {
          // for (let i = 0; i < temp_photos.length; i++){
          // //  tapa.venue.id === temp_photos[i].photos.id
          // }
          this.setState({tapas: temp_tapas})
        })
    }
  };

  updateResults = (foundTapas) => {
    this.setState({tapas: foundTapas})
  };

  render() {
    return (
      <div>
        <Route exact path="/" render={ () => (
          <div className="row">
            <div className="col-12 col-md-6">
              <SearchField
                tapas={this.state.tapas}
                location={this.state.location}
                update={this.updateResults}/>
              <TapasList
                tapas={this.state.tapas}
                // photos_ids={this.state.photos_ids}
                photos={this.state.photos}
              />
            </div>
            <div className="map-container col-12 col-md-6">
              <MapField/>
            </div>
          </div>
        )}
        />
        <Route path="/favourites" render={({history}) => (
          <Favourites/>
        )}/>

      </div>
    );
  }
}

export default App;


