import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import SearchField from './SearchField'
import MapField from './MapField'
// import * as FoursquareAPI from './utils/FoursquareAPI'

const api = "https://api.foursquare.com/v2/venues/explore?";
const apiUpToDate = "20181204"; // Date I built this app, tells FourSquare to use an api version compatible with this date
const limitResponses = "5"; // Number of responses (I think maximum is 50)
const clientid = "client_id=CLIENT_ID";
const id = "JX2GROLYRGSEKWBDR5X5MITPUUZAH3NEL5BLKYGTF5RBT5IA";
const clientSecret = "&client_secret=CLIENT_SECRET";
const secret = "XN3KVA302HH1TJ2P1OPH3WUWKGRXVKMX2NWNPQBTXOZSKPPC";


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      query: "",
      restaurants: [],
      location: {
        lat: 50.121870,
        lng: 8.689550
      }
    }
  }

  exploreApi = (loc) =>
    fetch(`${api}client_id=${id}&client_secret=${secret}&v=${apiUpToDate}&limit=${limitResponses}&ll=${loc.lat},${loc.lng}`, {})
      .then((res) => {
        return res.json();
      });
    // .then( (myJson) => {
    //   console.log(JSON.stringify(myJson))
    // })
    // .catch( (err) => {
    //   console.log("Fetch error", err)
    // });


  // Lifecycle event, fetches restaurants from FourSquare API
  componentDidMount(){
    // FourAPI.getAll().then( (restaurants) => {
    //   this.setState({restaurants: restaurants})
    // })
    // FourAPI.exploreArea(this.state.location)
    // FoursquareAPI.exploreApi(this.state.location);
    this.exploreApi(this.state.location)
      .then( (myJson) => {console.log(JSON.stringify(myJson))} )
      .catch( (err) => {
        console.log("On App error", err)
      })
  }

  render() {
    return (
      <div>
        <div className="row">
          <div className="col-12 col-md-6">
            <SearchField/>
            {/*<ListRestaurants/>*/}
          </div>
          <div className="col-12 col-md-6 map-container">
            <MapField/>
          </div>
        </div>
      </div>
    );
  }
}

export default App;


