/* global google */
import React, { Component } from 'react';
import PropTypes from "prop-types";

class ContactBody extends Component {

  static propTypes = {
    tapas: PropTypes.array,
  };

  constructor(props){
    super(props)
    this.state = {
      mapIsReady: false,
    };
  }

  getGoogleMaps() {
    // If we haven't already defined the promise, define it
    if (!this.googleMapsPromise) {
      this.googleMapsPromise = new Promise((resolve) => {
        // Add a global handler for when the API finishes loading
        window.resolveGoogleMapsPromise = () => {
          // Resolve the promise
          resolve(google);

          // Tidy up
          delete window.resolveGoogleMapsPromise;
        };

        // Load the Google Maps API
        const script = document.createElement("script");
        const API = 'AIzaSyB8uwCpGazZQ4JvdwNQP9Jt0GH0FukWd-M';
        script.src = `https://maps.googleapis.com/maps/api/js?key=${API}&callback=resolveGoogleMapsPromise`;
        script.async = true;
        document.body.appendChild(script);
      });
    }

    // Return a promise for the Google Maps API
    return this.googleMapsPromise;
  }

  populateInfoWindow = (marker, infoWindow, map) => {
    if (infoWindow.marker != marker){
      infoWindow.marker = marker;
      infoWindow.setContent(`<div>${marker.title}</div>`);
      infoWindow.open(map, marker);

      // infoWindow.addListener('closeclick', ()=>{
      //   infoWindow.setMarker(null);
      // })
    }
  };

  componentWillMount() {
    // Start Google Maps API loading since we know we'll soon need it
    this.getGoogleMaps();
    console.log("tapas 1", this.props.tapas);
  }

  componentDidMount() {
    // Once the Google Maps API has finished loading, initialize the map
    // NOTE: Moved content to componentDidUpdate() because props was still empty.
    console.log("tapas 2", this.props.tapas);
  }

  componentDidUpdate(){
    console.log("tapas 3", this.props.tapas);

    const {tapas} = this.props;
    let markers;

    // Once the Google Maps API has finished loading,
    // AND props loaded -> initialize the map
    this.getGoogleMaps().then((google) => {

      const center = {lat: 50.121870, lng: 8.689550};
      const map = new google.maps.Map(document.getElementById('map'), {
        zoom: 13,
        center: center
      });

      let largeInfoWindow = new google.maps.InfoWindow();

      markers = tapas.map(tapa => {
        let marker = new google.maps.Marker({
          map: map,
          position: {lat: tapa.venue.location.lat, lng: tapa.venue.location.lng},
          title: tapa.venue.name,
          animation: google.maps.Animation.DROP,
          id: tapa.venue.id
        });
        marker.addListener('click', () => {
          console.log("marker this?: ", this)
          this.populateInfoWindow(marker, largeInfoWindow, map);
        });
        return marker;
      });

      // const infoWindow = new google.maps.InfoWindow({
      //   content: "Test text"
      // });



    });
  }

  render() {
    return (
      <div>
        <h1>Contact</h1>
        <div id="map" style={{width: 400, height: 300}}></div>
      </div>
    )
  }
}

export default ContactBody;
