import React, { Component } from 'react'
import PropTypes from 'prop-types'

class FilterChanger extends Component {
  static propTypes = {
    // currentShelf: PropTypes.string.isRequired,
    // onUpdateBook: PropTypes.func.isRequired
  };

  constructor(props) {
    super(props);
    this.state = { theme: null };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    // this.props.onUpdateBook(event.target.value);
    // this.setState({value: event.target.value});
  }

  chooseTheme = (theme, evt) => {
    evt.preventDefault();
    console.log(evt);
    this.setState({ theme });
  };

  render(){

    const { theme } = this.state;
    const themeClass = theme ? theme.toLowerCase() : 'secondary';


    return <div>
        <div className="btn-group">
          <button type="button" className={`btn btn-${themeClass}`}>{ theme || 'Choose' } Theme</button>
          <button type="button" className={`btn btn-${themeClass} dropdown-toggle`} data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            <span className="sr-only">Toggle Theme Dropdown</span>
          </button>
          <div className="dropdown-menu">
            <a className="dropdown-item" href="#" onClick={e => this.chooseTheme('Primary', e)}>First item</a>
            <a className="dropdown-item" href="#" onClick={e => this.chooseTheme('Secondary', e)}>Second item</a>
          </div>
        </div>




        {/*<div className="btn btn-primary">*/}
          {/*<select value={this.state.value} onChange={this.handleChange}>*/}
            {/*<option className="dropdown-item" value="move" disabled>Move to...</option>*/}
            {/*<option className="dropdown-item" value="currentlyReading">Currently Reading</option>*/}
            {/*<option value="wantToRead">Want to Read</option>*/}
            {/*<option value="read">Read</option>*/}
            {/*<option value="none">None</option>*/}
          {/*</select>*/}
        {/*</div>*/}

      </div>


  }
}

export default FilterChanger;
