import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import SearchField from './SearchField'
import MapField from './MapField'
import * as FoursquareAPI from './utils/FoursquareAPI'


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      query: "",
      tapas: [],
      location: {
        lat: 50.121870,
        lng: 8.689550
      }
    }
  }

  // Lifecycle event, fetches restaurants from FourSquare API
  componentDidMount(){
    // FoursquareAPI.exploreApi(this.state.location)
    //   .then( (myJson) => {console.log(JSON.stringify(myJson))} )
    //   .catch( (err) => {
    //     console.log("On App error", err)
    //   })

    FoursquareAPI.getTapas(this.state.location)
      .then( myJson => {
        console.log(myJson.response);
        this.setState({tapas: [myJson.response.groups]})
    }).catch( err => { console.log(err)} )
  }

  render() {
    return (
      <div>
        <div className="row">
          <div className="col-12 col-md-6">
            <SearchField/>
            {/*<ListRestaurants/>*/}
          </div>
          <div className="col-12 col-md-6 map-container">
            <MapField/>
          </div>
        </div>
      </div>
    );
  }
}

export default App;


