const coffee = "&v=20180323&limit=1";
const latlon = "&ll=40.7243,-74.0018";
const q = "&query=coffee";

let explore = "https://api.foursquare.com/v2/venues/explore";
let venuApi = 'https://api.foursquare.com/v2/venues';
let apiUrl = "https://api.foursquare.com/v2";
let apiFeature = "/venues";



// 'https://api.foursquare.com/v2/venues/explore?client_id=CLIENT_ID&client_secret=CLIENT_SECRET&v=20180323&limit=1&ll=40.7243,-74.0018&query=coffee'

// Generate a unique token for storing your bookshelf data on the backend server.
let token = localStorage.token;
if (!token)
  token = localStorage.token = Math.random().toString(36).substr(-8);

const headers = {
  'Accept': 'application/json',
  'Authorization': token
};

export const exploreArea = (location) => {

  let exploreLoc = explore + "?ll=" + location.lat.toString() + "," + location.lng.toString();
  console.log("explore argument: ", exploreLoc);

  fetch(exploreLoc)
    .then( (response) => {
      console.log(response)
    })
    .catch( (err) => {
     console.log(err)
  })
};

export const exploreApi = (loc) => {
  return fetch(`${api}client_id=${id}&client_secret=${secret}&v=${apiUpToDate}&limit=${limitResponses}&ll=${loc.lat},${loc.lng}`, {})
    .then((res) => res.json())
    .catch( (err) => {
      console.log("Fetch error", err)
    });
};

export const getTapas = (loc) => {

  return fetch(`${api}client_id=${id}&client_secret=${secret}&v=${apiUpToDate}&limit=${limitResponses}&ll=${loc.lat},${loc.lng}&query=tapas`, {})
    .then((res) => res.json())
    .catch( (err) => {
      console.log("getTapas error", err)
    });
};

export const getPhotos = (idArr) => {
  console.log("idArr ", idArr);
  // return Promise.all(idArr.map( id_photo => {
  //     fetch(`${apiUrl}${apiFeature}/${id_photo}/photos?client_id=${id}&client_secret=${secret}&v=${apiUpToDate}`)
  // })).then( res => {
  //   console.log(res);
  //   // res.json();
  // });

  let fetchArray = idArr.map( idPhoto =>
    (`${apiUrl}${apiFeature}/${id_photo}/photos?client_id=${id}&client_secret=${secret}&v=${apiUpToDate}`));

  console.log("fetchArray: ", fetchArray);
  let id_photo = idArr[1];
  // let urlString = apiUrl + apiFeature + "/" + venue_id + '/photos?' +
  //   "client_id=" + id + "&client_secret=" + secret + '&v=' + apiUpToDate;
  //`${venuApi}/${idArr[0]}?client_id=${id}&client_secret=${secret}`

  return fetch(`${apiUrl}${apiFeature}/${id_photo}/photos?client_id=${id}&client_secret=${secret}&v=${apiUpToDate}`)
    .then( res => res.json() )
    .catch( err => {
      console.log("Photo error", err)
    })
};

export const getQuery = (quer) => {
  fetch(api)
    .then((res) => res.json())
    .catch(function() {
      // Code for handling errors
    });
};

export const getAll = () =>
  fetch(`${api}/books`, { headers })
    .then(res => res.json())
    .then(data => data.books);




